package testchain

import (
	"math/big"

	"github.com/ethereum/go-ethereum/common"
)

// DeployERC20 deploys an ERC-20 token to the blockchain.
// The token has the following attributes:
//  - Name: Test ERC-20 Token
//  - Symbol: ERC20
//  - Decimals: user-defined
//  - Total supply: 100000000000000000000000000
func DeployERC20(tc *TestChain, decimals uint8, creator common.Address) (*common.Address, error) {
	bin := common.FromHex("")
	constructor := common.FromHex("")
	constructor[95] = byte(decimals)

	// Deploy the contract
	data := make([]byte, 0)
	data = append(data, bin...)
	data = append(data, constructor...)
	return tc.DeployContract(creator, big.NewInt(0), data)
}
